import React, { useState, useEffect } from 'react';
import { useRouteMatch } from 'react-router-dom'
import { useHistory } from 'react-router-dom'
import axios from 'axios';
import * as yup from 'yup';
import formSchema from './validation/formSchema'
import './App.css';
import './Form.css';

const initialFormValues ={
  ///// DROPDOWN /////
  dropdown: '',
  ///// RADIO BUTTONS /////
  radio: '',
  ///// CHECKBOXES /////
  pepperoni: false,
  sausage: false,
  spicy_italian: false,
  onions: false,
  tomatoes: false,
  spinach: false,
  bacon: false,
  mushrooms: false,
  ////TEXTAREA/////
  text_area: ''
}
const initialFormErrors = {
  username: '',
  email: '',
  role: '',
  radio: '',
}
const initialUsers = []
const initialDisabled = true;

export default function Form(props){
  const [users, setUsers] = useState(initialUsers)    
  const [formValues, setFormValues] = useState(initialFormValues)
  const [formErrors, setFormErrors] = useState(initialFormErrors) 
  const [disabled, setDisabled] = useState(initialDisabled)
  const { url, path } = useRouteMatch()
  

  const postNewUser = newUser => {
    axios.post('fakeurl', newUser)
    .then(res=> {
      const newData = [res.data, ...users];
      console.log(res.data)
      setUsers(newData)
      // console.log(newData)
    })
    .catch(err => {
      console.log(err)
    })
    setFormValues(initialFormValues)
  }
  const inputChange = (name, value) => {
    yup.reach(formSchema, name)
    .validate(value)
    .then(()=> {
      setFormErrors({...formErrors, [name]: ''})
    })
    .catch(err => {
      setFormErrors({...formErrors, [name]: err.errors[0]})
    })
    setFormValues({...formValues, [name]: value})
  }

  const formSubmit = () => {
    const newUser = {
      dropdown: formValues.dropdown.trim(),
      sauces: formValues.sauces.trim(),
      password: formValues.password.trim(),
      toppings: ['pepperoni', 'sausage', 'spicy sausage', 'onion', 'tomatoes', 'mushrooms','spinach', 'bacon'].filter(accepted => formValues[accepted])
    }
    postNewUser(newUser);
  }
  useEffect(() => {
    formSchema.isValid(formValues).then(valid => setDisabled(!valid));
  }, [formValues])

  const history = useHistory();
  const onSubmit = evt => {
    evt.preventDefault()
    // submit()
    history.push('/confirmation')
  }

  const onChange = evt => {
    const { name, value, type, checked } = evt.target;
    const valueToUse = type === 'checkbox' ? checked : value;
    inputChange(name, valueToUse);
  }

  console.log({url})
  return (
    <div className='item-wrapper'>
    
      <form className='form' onSubmit={onSubmit}>
        <div className='form-group submit'>
    
        {/* <div className='errors'>
            <div>{errors.username}</div>
            <div>{errors.email}</div>
            <div>{errors.password}</div>
            <div>{errors.tos}</div>
        </div> */}
    
        </div>

        <div className='form-group'>
        <h2>Build Your Own Pizza</h2>
        {/* DROPDOWN */}
        <div className="label-name">
          <h3>Choice of Size</h3>
        <label>Required
          <select
            onChange={onChange}
            value={formValues.dropdown}
            name='dropdown'
            >
            <option value=''>- Select -</option>
            <option value='sm'>'SM'</option>
            <option value='med'>'MED'</option>
            <option value='lg'>'LG'</option>
            <option value='xl'>'XL'</option>
          </select>
          </label>
        </div>
        <h3>Choice of Sauce</h3>
        <p>Required</p>
        {/* RADIO BUTTON */}
        <div className="form-group-radio">
        <label>Original Red
          <input
            type='radio'
            name={'radio'}
            onChange={onChange}
            value='original'
            checked={'original' === formValues.radio}
          />
        </label>
        <label>Garlic Ranch
          <input
            type='radio'
            name='radio'
            onChange={onChange}
            value='garlic_ranch'
            checked={'garlic_ranch' === formValues.radio}
          />
        </label>
        <label>Parmesan Alfredo
          <input
            type='radio'
            name='radio'
            onChange={onChange}
            value='parmesan_alfredo'
            checked={'parmesan_alfredo' === formValues.radio}
          />
        </label>
        <label>BBQ     
          <input
            type='radio'
            name='radio'
            onChange={onChange}
            value='bbq'
            checked={'bbq' === formValues.radio}
          />
        </label>
        </div>

  
    </div>

        <h3>Choice of Toppings</h3>
        <div className='form-group-checkbox-labels'>

        {/* ////////// CHECKBOXES ////////// */}
        <label>Pepperoni
            <input 
            type='checkbox'
            name='pepperonitos'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        <label>Sausage
            <input 
            type='checkbox'
            name='sausage'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        <label>Spicy Italian Sausage
            <input 
            type='checkbox'
            name='spicy_italian'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        <label>Onions
            <input 
            type='checkbox'
            name='onions'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        <label>Tomatoes
            <input 
            type='checkbox'
            name='tomatoes'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        <label>Spinach
            <input 
            type='checkbox'
            name='spinach'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        <label>Bacon
            <input 
            type='checkbox'
            name='bacon'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        <label>Mushrooms
            <input 
            type='checkbox'
            name='mushrooms'
            onChange={onChange}
            checked={formValues.toppings}
            />
        </label>
        </div>
        <div className="text-area">
          <label> Special Instructions
            <input type='text-area' name='text_area'></input>

          </label>
        </div>
        <div className='form-group submit'>
        <button disabled={disabled}>SUBMIT</button>
        </div>
       
    </form>    
    </div>
  );
}
