import React, { useState, useEffect, useHistory, useRouteMatch } from 'react';
import { Route, Link, Switch } from 'react-router-dom';
import Home from './Home';
import Form from './Form';
import Confirmation from './Confirmation';
import axios from 'axios';
import Footer from './Footer';
import * as yup from 'yup';
import formSchema from './validation/formSchema'
import './App.css';


const initialFormValues ={
  ///// DROPDOWN /////
  dropdown: '',
  ///// RADIO BUTTONS /////
  sauces: '',
  ///// CHECKBOXES /////
  pepperoni: false,
  sausage: false,
  spicy_italian: false,
  onions: false,
  tomatoes: false,
  spinach: false,
  bacon: false,
  mushrooms: false,
  ////TEXTAREA/////
  text_area: ''
}
const initialFormErrors = {
  dropdown: '',
  sauces: '',
  text_area: '',
}
const initialDisabled = true;
const initialOrders = [];

export default function App(){ 
  const [orders, setOrders] = useState(initialOrders) 
  const [formValues, setFormValues] = useState(initialFormValues)
  const [formErrors, setFormErrors] = useState(initialFormErrors) 
  const [disabled, setDisabled] = useState(initialDisabled)
  const [visible, setVisibility] = useState(false)
  // const url  = useRouteMatch()
  // const history = useHistory();

  const postNewOrder = newOrder => {
    axios.post(`https://reqres.in/api/orders`, newOrder)
    .then(res=> {
      const newData =[res.data, ...orders];
     setOrders(newData)
      console.log(newData)
    })
    .catch(err => {
      console.log(err)
    })
    setFormValues(initialFormValues)
  }
  const inputChange = (name, value) => {
    yup.reach(formSchema, name)
    .validate(value)
    .then(()=> {
      setFormErrors({...formErrors, [name]: ''})
    })
    .catch(err => {
      setFormErrors({...formErrors, [name]: err.errors[0]})
    })
    setFormValues({...formValues, [name]: value})
    formErrors ? setVisibility(true) : setVisibility(false);
  }
 

  const formSubmit = () => {
  //  history.push(`pizza/confirmation`);
    const newOrder = {
      dropdown: formValues.dropdown.trim(),
      sauces: formValues.sauces.trim(),
      toppings: formValues.toppings ? [
        'pepperoni',
        'sausage', 
        'spicy_italian', 
        'onion', 
        'tomatoes',
        'mushrooms',
        'spinach', 
        'bacon'].filter(topping => {
        console.log(formValues[topping])
        return formValues[topping]
      }): "",
      text_area: formValues.text_area.trim()
      
    }
    // setOrders(orders.concat(newOrder))
    postNewOrder(newOrder); 
  }
  useEffect(() => {
    formSchema.isValid(formValues).then(valid => {
      console.log(valid)
      return setDisabled(!valid)}
   )}, [formValues])
  //  console.log(formValues)

  const onSubmit = evt => {
    evt.preventDefault()
    formSubmit()
    
  }

  const onChange = evt => {
    const { name, value, type, checked } = evt.target;
    const valueToUse = type === 'checkbox' ? checked : value;
    inputChange(name, valueToUse);
  }
  return (
    <div className='App'>
      <nav>
      <h1 className='store-header'>KHANA PIES</h1>
      <div className='nav-links'>
          <Link to="/">Home</Link>
          <Link to="/pizza">Order Online</Link>
        </div>
      </nav>
    <Switch>
      <Route exact path='/pizza/confirmation'>
          <Confirmation orders={orders}/>
      </Route>
      <Route exact path='/pizza'>
        <Form submit={onSubmit} change={onChange} formValues={formValues} disabled={disabled} errors={formErrors}/>
      </Route>
      <Route exact path='/'>
        <Home/>
      </Route>
    </Switch>
    <Footer/>
    </div>
  )
}
