// Here goes the schema for the form
import * as yup from 'yup';

const formSchema = yup.object().shape({
    dropdown: yup.string().oneOf(['sm','med', 'lg', ],'Please select a size')
        .required('Size is required'),
    toppigs: yup.boolean().oneOf(['pepperoni', 'sausage', 'spicy sausage', 'onion', 'tomatoes', 'mushrooms','spinach', 'bacon'],'Please select a topping')
})

export default formSchema;
