// Here goes the schema for the form
import * as yup from 'yup';

const formSchema = yup.object().shape({
    dropdown: yup.string().oneOf(['pepperoni', 'sausage', 'spicy sausage', 'onion', 'tomatoes', 'mushrooms','spinach', 'bacon'],'Please select a size')
        .required('Size is required'),
    email: yup.string().email().required('Email is required'),
    password: yup.string('Must be a valid email').trim()
        .required('Password is required')
        .min(8, "Password must be at least 8 characters"),
    tos: yup.boolean().oneOf([true],'Acceptance of Terms of Service is required'),
})

export default formSchema;
